mapper.select:
SELECT g.group_name
FROM group_member m, groups g
WHERE m.group_no = g.group_no
AND g.group_no = #{groupNo}
AND m.user_no = #{userNo}

SELECT group_leader
FROM groups
WHERE group_no = #{groupNo}

SELECT COUNT(group_no)
FROM group_member
WHERE user_no = #{userNo}

mapper.update:
UPDATE GROUP_MEMBER
SET GROUP_ORDER = #{order}
WHERE USER_NO = #{userNo}
AND GROUP_NO = #{groupNo}

UPDATE groups
SET 


			group_leader = #{groupLeader}


			group_name = #{groupName}


WHERE group_no = #{groupNo}

UPDATE group_member
SET boss_check = 0
WHERE group_no = #{groupNo}

mapper.select:
SELECT  count(user_no)
FROM group_member
WHERE group_no = #{groupNo}
AND user_no = #{userNo}

SELECT group_member_no, u.user_no, group_no, boss_check, user_name, user_birth_year,
CASE WHEN user_birth_year > "30_start" THEN '20대 이하' WHEN user_birth_year > "40_start" AND user_birth_year <= "30_start" THEN '30대' WHEN user_birth_year > "50_start" AND user_birth_year <= "40_start" THEN '40대' WHEN user_birth_year <= "50_start" THEN '50대 이상' END user_age,
CASE WHEN user_sex = 'male' THEN '남자' WHEN user_sex = 'female' THEN '여자' END user_sex, group_order FROM group_member m, users u, ( SELECT TO_NUMBER(TO_CHAR(SYSDATE, 'YYYY')) - 29 "30_start", TO_NUMBER(TO_CHAR(SYSDATE, 'YYYY')) - 39 "40_start", TO_NUMBER(TO_CHAR(SYSDATE, 'YYYY')) - 49 "50_start" FROM DUAL ) WHERE m.user_no = u.user_no AND group_member_no = #{groupMemberNo}

mapper.delete:
DELETE FROM group_member
WHERE user_no = #{userNo}
AND group_no = #{groupNo}

mapper.update:
UPDATE group_member
SET group_order = group_order - 1
WHERE user_no = #{userNo}
AND group_order > #{groupOrder}
